mapper.select:
SELECT *

FROM (SELECT ROWNUM AS rowno, t.*

      FROM t_firstpage_testing t

      WHERE t.batch_id=#{batchId}

        AND ROWNUM <= #{upperBound}) table_alias

WHERE table_alias.rowno >= #{lowerBound}

SELECT count(t.T_FIRST_PAGE_TESTING_ID)
from t_firstpage_testing t
WHERE t.BATCH_ID=#{batchId}

SELECT distinct t.T_FIRST_PAGE_TESTING_ID, t.org_id,t.org_name,t.card_no,t.medicare_card,t.pay_way_code,t.actual_in_days ,t.case_no,t.sex_code,t.create_time,t.out_dept_code,t.batch_id,t.fname FROM (SELECT a.T_FIRST_PAGE_TESTING_ID, a.org_id,a.org_name,a.card_no,a.medicare_card,a.pay_way_code,a.actual_in_days ,a.case_no,a.sex_code,a.create_time,a.out_dept_code,a.batch_id,a.fname FROM t_firstpage_testing a LEFT JOIN t_firstoutdiag_testing b on a.T_FIRST_PAGE_TESTING_ID=b.T_FIRST_PAGE_TESTING_ID
a.UP_ORG_ID in
#{obj}
and b.diagnosis_code=#{diagnosisCode}
and a.fname like concat(concat('%',#{fname}),'%')
and a.case_no like concat(concat('%',#{caseNo}),'%')
and a.org_name like concat(concat('%',#{orgName}),'%')
) t LEFT JOIN t_firstoutoper_testing c on t.T_FIRST_PAGE_TESTING_ID=c.T_FIRST_PAGE_TESTING_ID
c.operation_code=#{operationCode}
GROUP by t.T_FIRST_PAGE_TESTING_ID, t.org_id,t.org_name,t.card_no,t.medicare_card,t.pay_way_code,t.actual_in_days ,t.case_no,t.sex_code,t.create_time,t.out_dept_code,t.batch_id,t.fname
